-- generates a noun
function gen.noun(min, max)
  local index = math.random(#gen.nouns)
  return gen.nouns[index]
end

-- generates a verb
function gen.verb(min, max)
  local index = math.random(#gen.verbs)
  return gen.verbs[index]
end

-- generates and adjective
function gen.adjective(min, max)
  local index = math.random(#gen.adjectives)
  return gen.adjectives[index]
end

-- transforms a seed [0, 2^53 - 1] into a phrase
function gen.seedToPhrase(seed)
  local seed = seed

  local function divSeed(tab)
    local s = seed % #tab
    seed = (seed - s) / #tab
    return tab[s + 1]
  end
  
  local noun1 = divSeed(gen.nouns)
  local noun2 = divSeed(gen.nouns)

  local adjective1 = divSeed(gen.adjectives)
  local adjective2 = divSeed(gen.adjectives)
  
  local verb = divSeed(gen.verbs)

  local vowels = 'aeiou'
  local article1 = vowels:find(adjective1:sub(1, 1)) and "an" or "a"
  local article2 = vowels:find(adjective2:sub(1, 1)) and "an" or "a"

  return string.format('%s %s %s %s %s %s %s', article1, adjective1, noun1, verb .. 's', article2, adjective2, noun2)
end

-- transforms a phrase generated using this module into a seed [0, 2^53 - 1]
function gen.phraseToSeed(phrase)
  local adjective1, noun1, verb, adjective2, noun2 = 
    phrase:match('an? ([A-Za-z-]+) ([A-Za-z-]+) ([A-Za-z-]+)s an? ([A-Za-z-]+) ([A-Za-z-]+)')

  local function indexOf(tab, value)
    for i, v in ipairs(tab) do
      if v == value then return i - 1 end
    end
  end

  local seed = 0

  seed = indexOf(gen.verbs, verb)
  seed = seed * #gen.adjectives + indexOf(gen.adjectives, adjective2)
  seed = seed * #gen.adjectives + indexOf(gen.adjectives, adjective1)
  seed = seed * #gen.nouns + indexOf(gen.nouns, noun2)
  seed = seed * #gen.nouns + indexOf(gen.nouns, noun1)

  return seed
end

-- generates a phrase
function gen.phrase(seed)
  return gen.seedToPhrase(seed or math.random(gen.phraseCount() - 1))
end

-- prints the total amount of possible phrases
function gen.phraseCount()
  return ((#gen.nouns)^2 * (#gen.adjectives)^2 * #gen.verbs)
end

return gen
